######################################################### input information runnersprint("Please enter names: ",end="")namelist = input().split(",")       #seprate namesnew_name_list=list()for name in namelist:               #delete white space    name = name.strip()    new_name_list.append(name)new_name_list=set(new_name_list)               #delete extra namenew_name_list = sorted(new_name_list)info_runners = list()#print(new_name_list)for i in new_name_list:    runner = dict()    id=new_name_list.index(i)    runner['id']=id+1    runner['name'] = i    print("enter duration time for",i,':',end="")    duration_time = int(input())    runner['time']=duration_time            #min    print("enter your distance in km for",i,':',end="")    dis=int(input())         #km    runner['distance']=dis    _speed = (dis)/(duration_time/60)    runner['speed']=_speed    _pace=duration_time/dis    runner['pace']=_pace    if dis<10:        runner['classify finisher'] = 'null'    elif 10 <= dis <21.0975:        runner['classify finisher'] ='+10k'    elif 21.0975 <= dis <42.195:        runner['classify finisher'] ='half marathon'    elif 42.195 <= dis <60:        runner['classify finisher'] ='marathon'    else :        runner['classify finisher'] = "ultra"    info_runners.append(runner)print(info_runners)####################################################################  speed base on classclassify_10k={d['name']:d['speed'] for d in info_runners if d['classify finisher']=='+10k'}#print(classify_10k)classify_half={d['name']:d['speed'] for d in info_runners if d['classify finisher']=='half marathon'}#print(classify_half)classify_marathon={d['name']:d['speed'] for d in info_runners if d['classify finisher']=='marathon'}#print(classify_marathon)classify_ultra={d['name']:d['speed'] for d in info_runners if d['classify finisher']=='ultra'}#print(classify_ultra)i=0print('top 3 fastest in +10k is:')for k in sorted(classify_10k, key=classify_10k.get, reverse=True):   print(k, classify_10k[k])   i=i+1   if i==3:     breaki=0print('top 3 fastest in half marathon is:')for k in sorted(classify_half, key=classify_half.get, reverse=True):   print(k, classify_half[k])   i=i+1   if i==3:     breaki=0print('top 3 fastest in marathon is:')for k in sorted(classify_marathon, key=classify_marathon.get, reverse=True):   print(k, classify_marathon[k])   i=i+1   if i==3:     breaki=0print('top 3 fastest in ultra is:')for k in sorted(classify_ultra, key=classify_ultra.get, reverse=True):   print(k, classify_ultra[k])   i=i+1   if i==3:     breakall_fasted={**classify_10k,**classify_half,**classify_marathon,**classify_ultra}#print(all_fasted)print("the fastest is :",end="")i=0for k in sorted(all_fasted, key=all_fasted.get, reverse=True):   print(k,all_fasted[k])   i=i+1   if i>0:     breakhighest_distance=[d['distance'] for d in info_runners]highest_distance=sorted(highest_distance)print('highest distance is:',highest_distance[-1])#############################################################